import React from 'react';
import Spotify from 'spotify-web-api-js';

const spotifyWebApi = new Spotify();

class Main extends React.Component{
    constructor(props) {
        super(props);
        const params = this.getHashParams();

        this.handleSearchChange = this.handleSearchChange.bind(this);
        this.handlePlaylistName = this.handlePlaylistName.bind(this);

        this.state = {
          loggedIn: params.access_token ? true : false,
          artists: '',
          tracks: [],
          trackuri: [],
          playlistName: '',
          device_id: '',
          user_id: '',
        }

        if (params.access_token) {
          spotifyWebApi.setAccessToken(params.access_token)
        }
      }

      getHashParams() {
        var hashParams = {};
        var e, r = /([^&;=]+)=?([^&;]*)/g,
            q = window.location.hash.substring(1);
        while ( e = r.exec(q)) {
           hashParams[e[1]] = decodeURIComponent(e[2]);
        }
        return hashParams;
      }

      componentDidMount() {
        spotifyWebApi.getMe()
        .then((res) => {
          this.setState({
            user_id: res.id
          })
        })
      }

      handleSearchChange(event) {
        this.setState({
          artists: event.target.value
        })
      }

      handleSearchArtistId() {
        this.setState({tracks: []})
        let artists = this.state.artists.split(',');
        artists.map(artistName => (
          spotifyWebApi.searchArtists(artistName) 
          .then((res) => {
            let artistId = res.artists.items[0].id
            spotifyWebApi.getArtistTopTracks(artistId, "CA", { limit: 15 })
            .then((res) => {
              for (let i = 0; i < res.tracks.length; i++) {
                this.setState(state => {
                  const tracks = state.tracks.concat(res.tracks[i])
                  const trackuri = state.trackuri.concat(res.tracks[i].uri)
                  return {tracks, trackuri}
                })
              }
            })
          }) 
        ))
      }

      handlePlaylistName(event) {
        this.setState({
          playlistName: event.target.value
        })
      }

      handleCreatePlaylist() {
        spotifyWebApi.createPlaylist(this.state.user_id, {name: this.state.playlistName})
        .then((res) => {
          spotifyWebApi.addTracksToPlaylist(this.state.user_id, res.id, this.state.trackuri)
        })
      }
    render() {
        return (
            <div>
            {this.state.loggedIn ? "" : 
            <a href="http://localhost:8888">
              <button>
                Log into Spotify
              </button>
            </a>
            }
            {this.state.loggedIn ? 

            <div>
              <form>
                <input 
                  type="text" 
                  placeholder="Enter artists name" 
                  value={this.state.artists} 
                  onChange={this.state.handleSearchChange}
                />
                <button type="button" onClick={() => this.state.handleSearchArtistId}>
                  Search top tracks
                </button>
              </form>

              <div>
                {this.state.tracks.map(track => (
                  <li key={track.id}>{track.name}</li>
                ))}
                { this.state.tracks.length > 0 ? 
                <div>
                  <input
                    type="text"
                    placeholder="Playlist Name"
                    value={this.state.playlistName}
                    onChange={this.state.handlePlaylistName(event)}
                  /> 
                  <button onClick={() => this.state.handleCreatePlaylist}>
                  Create playlist
                  </button>
                </div>
              : ""}
              </div>
            </div> : ""}
        </div>
        )
    }
}

export default Main
