import React from 'react';
import Spotify from 'spotify-web-api-js';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import '../Main.css';

const spotifyWebApi = new Spotify();

class Main extends React.Component{
    constructor(props) {
        super(props);
        const params = this.getHashParams();

        this.handleSearchChange = this.handleSearchChange.bind(this);
        this.handlePlaylistName = this.handlePlaylistName.bind(this);

        this.state = {
            loggedIn: params.access_token ? true : false,
            artists: '',
            tracks: ['test'],
            trackuri: [],
            playlistName: '',
            device_id: '',
            user_id: '',
        }

        if (params.access_token) {
            spotifyWebApi.setAccessToken(params.access_token)
        }
    }

    getHashParams() {
        var hashParams = {};
        var e, r = /([^&;=]+)=?([^&;]*)/g,
            q = window.location.hash.substring(1);
        while ( e = r.exec(q)) {
            hashParams[e[1]] = decodeURIComponent(e[2]);
        }
        return hashParams;
    }

    componentDidMount() {
        spotifyWebApi.getMe()
        .then((res) => {
            this.setState({
            user_id: res.id
            })
        })
    }

    handleSearchChange(event) {
        this.setState({
            artists: event.target.value
        })
    }

    handleSearchArtistId() {
        this.setState({tracks: []})
        let artists = this.state.artists.split(',');
        artists.map(artistName => (
            spotifyWebApi.searchArtists(artistName) 
            .then((res) => {
            let artistId = res.artists.items[0].id
            spotifyWebApi.getArtistTopTracks(artistId, "CA")
            .then((res) => {
                for (let i = 0; i < res.tracks.length; i++) {
                this.setState(state => {
                    const tracks = state.tracks.concat(res.tracks[i])
                    const trackuri = state.trackuri.concat(res.tracks[i].uri)
                    return {tracks, trackuri}
                })
                }
            })
            }) 
        ))
    }

    handlePlaylistName(event) {
        this.setState({
            playlistName: event.target.value
        })
    }

    handleCreatePlaylist() {
        spotifyWebApi.createPlaylist(this.state.user_id, {name: this.state.playlistName})
        .then((res) => {
            spotifyWebApi.addTracksToPlaylist(this.state.user_id, res.id, this.state.trackuri)
        })
    }

    render() {
        return (
            <div style={{backgroundColor: '#212121', height: '100vh', width: '100%'}}>
                {this.state.loggedIn ? "" : 
                <div style={{paddingTop: '5%'}}>
                    <a href="http://localhost:8888" className="loginbutton">
                        CLICK TO LOG IN
                    </a>
                </div>
                }
                {this.state.loggedIn ? 
                <div className="mainForm">
                    <form className="form">
{/*                         <input 
                        type="search"
                        className="artistInput"
                        placeholder="Search&#x2026;"
                        value={this.state.artists} 
                        onChange={this.handleSearchChange}
                        /> */}
                        <InputGroup className="artistInput" style={{width: '30%', textDecoration: 'none', borderRadius: '30px', boxSizing: 'border-box'}}>
                            <InputGroup.Prepend>
                            <InputGroup.Text>
                                <svg class="bi bi-search" width="1em" height="1em" viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                                    <path fill-rule="evenodd" d="M10.442 10.442a1 1 0 0 1 1.415 0l3.85 3.85a1 1 0 0 1-1.414 1.415l-3.85-3.85a1 1 0 0 1 0-1.415z"/>
                                    <path fill-rule="evenodd" d="M6.5 12a5.5 5.5 0 1 0 0-11 5.5 5.5 0 0 0 0 11zM13 6.5a6.5 6.5 0 1 1-13 0 6.5 6.5 0 0 1 13 0z"/>
                                </svg>
                            </InputGroup.Text>
                            </InputGroup.Prepend>
                            <FormControl style={{outline: 'none', boxShadow: 'none ', border: '1px solid #ccc '}}
                                type="search"
                                className="artistInput"
                                placeholder="Search&#x2026;"
                                value={this.state.artists} 
                                onChange={this.handleSearchChange}
                            />
                        </InputGroup>
                        <br/>
                        <button type="button" onClick={() => this.handleSearchArtistId()}>
                        Search top tracks
                        </button>
                    </form>

                    <div style={{alignItems: 'center'}}>
                        {this.state.tracks.map(track => (
                        <li key={track.id}>{track.name}</li>
                        ))}
                        { this.state.tracks.length > 0 ? 
                    <div style={{textAlign: 'center', alignContent: 'center'}}>
{/*                     <input
                        type="text"
                        placeholder="Playlist Name"
                        value={this.state.playlistName}
                        onChange={this.handlePlaylistName}
                    />  */}
                    <InputGroup style={{width: '20%', textDecoration: 'none', borderRadius: '30px', boxSizing: 'border-box', textAlign: 'center'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text>
                            <svg class="bi bi-collection-play" width="1em" height="1em" viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                                <path fill-rule="evenodd" d="M14.5 13.5h-13A.5.5 0 0 1 1 13V6a.5.5 0 0 1 .5-.5h13a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-.5.5zm-13 1A1.5 1.5 0 0 1 0 13V6a1.5 1.5 0 0 1 1.5-1.5h13A1.5 1.5 0 0 1 16 6v7a1.5 1.5 0 0 1-1.5 1.5h-13zM2 3a.5.5 0 0 0 .5.5h11a.5.5 0 0 0 0-1h-11A.5.5 0 0 0 2 3zm2-2a.5.5 0 0 0 .5.5h7a.5.5 0 0 0 0-1h-7A.5.5 0 0 0 4 1z"/>
                                <path fill-rule="evenodd" d="M6.258 6.563a.5.5 0 0 1 .507.013l4 2.5a.5.5 0 0 1 0 .848l-4 2.5A.5.5 0 0 1 6 12V7a.5.5 0 0 1 .258-.437z"/>
                            </svg>
                        </InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl style={{outline: 'none', boxShadow: 'none ', border: '1px solid #ccc', textAlign: 'center'}}
                            type="text"
                            placeholder="Playlist Name"
                            value={this.state.playlistName}
                            onChange={this.handlePlaylistName}
                        />
                    </InputGroup>
                    <button onClick={() => this.handleCreatePlaylist()}>
                    Create playlist
                    </button>
                </div>
                : ""}
              </div>
            </div> : ""}
        </div>
        )
    }
}

export default Main
