import React from 'react'; 
import logo from './logo.svg';
import './App.css';
import Spotify from 'spotify-web-api-js';

const spotifyWebApi = new Spotify();

class App extends React.Component { 
  constructor(props) {
    super(props);
    const params = this.getHashParams();

    this.handleSearchChange = this.handleSearchChange.bind(this);
    //this.handleSearchArtistId = this.handleSearchArtistId.bind(this);
    this.handleSearchAlbumId = this.handleSearchAlbumId.bind(this);
    this.randomTrack = this.randomTrack.bind(this);

    this.state = {
      loggedIn: params.access_token ? true : false,
      artists: '',
      artistId: [],
      albumId: [],
      nowPlaying: {
        name: 'Not checked',
        image: '',
      },
    }

    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token)
    }
  }

  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  componentDidMount() {
    spotifyWebApi.getMyCurrentPlaybackState()
    .then((response) => {
      this.setState({
        nowPlaying: {
          name: response.item.name,
          image: response.item.album.images[0].url
        }
      })
    })
    .catch((err) => {
      console.log(err)
      if (this.state.loggedIn) {
        this.setState({
          nowPlaying: {
            name: 'Please log in!'
          }
        })
      }

      else {
        this.setState({
          nowPlaying: {
            name: 'Currently not playing anything'
          }
        })
      }
    })
  }

  handleSearchChange(event) {
    this.setState({
      artists: event.target.value
    })
  }

  handleSearchAlbumId(artistId) {
    artistId.map(artistId => (
      spotifyWebApi.getArtistAlbums(artistId)
      .then((res) =>{
        console.log(res)
      })
    ))
  }

  randomTrack(trackArray) {
    const ran = Math.floor(Math.random() * trackArray.length)
    return trackArray[ran]
  }

  handleSearchArtistId() {
    let artists = this.state.artists.split(',');

    artists.map(artistName => (
      spotifyWebApi.searchArtists(artistName) 
      .then((res) => {
        this.setState({
          artistId: [...this.state.artistId, res.artists.items[0].id]
        })
        //this.state.artistId.push(res.artists.items[0].id)
      }) 
    ))
    console.log(this.state.artistId)

    this.state.artistId.map(artist => (
      spotifyWebApi.getArtistAlbums(artist)
      .then((res) =>{
        for (let i = 0; i < res.items.length; ++i) {
          this.state.albumId.push(res)
        }
      })
    ))
  }


  render() {
    return (
      <div className="App">
        <h1>Spotify Playlist Generator</h1>
        <a href="http://localhost:8888">
          <button>
            Log into Spotify
          </button>
        </a>
        <div>
          Now playing: {this.state.nowPlaying.name}
        </div>
        <div>
          <img src={this.state.nowPlaying.image} style={{width: 100}} />
        </div>
        <br/>
        <form>
          <input 
            type="text" 
            placeholder="Enter artists name" 
            value={this.state.artists} 
            onChange={this.handleSearchChange}
          />
          <button onClick={() => handleSearchArtistId()}>
            Generate Playlist
          </button>

        </form>
      </div> 
    );
  }
}

export default App;
