import React from 'react'; 
import logo from './logo.svg';
import './App.css';
import Spotify from 'spotify-web-api-js';

const spotifyWebApi = new Spotify();

class App extends React.Component { 
  constructor(props) {
    super(props);
    const params = this.getHashParams();

    this.handleSearchChange = this.handleSearchChange.bind(this);
    this.handlePlaylistName = this.handlePlaylistName.bind(this);

    this.state = {
      loggedIn: params.access_token ? true : false,
      artists: '',
      tracks: [],
      trackuri: [],
      playlistName: '',
      device_id: '',
      user_id: '',
      nowPlaying: {
        name: '',
        image: '',
      },
    }

    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token)
    }
  }

  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  componentDidMount() {
    spotifyWebApi.getMyCurrentPlaybackState()
    .then((response) => {
      spotifyWebApi.getMe()
      .then((res) => {
        this.setState({
          user_id: res.id
        })
      })
      this.setState({
        nowPlaying: {
          name: response.item.name,
          image: response.item.album.images[0].url
        }, 
        device_id: response.device.id
      })
    })

    .catch((err) => {
      console.log(err)
      if (this.state.loggedIn) {
        this.setState({
          nowPlaying: {
            name: 'Currently not playing anything'
          }
        })
      }

      else {
        this.setState({
          nowPlaying: {
            name: 'Please log in'
          }
        })
      }
    })
  }

  handleSearchChange(event) {
    this.setState({
      artists: event.target.value
    })
  }

  handleSearchArtistId() {
    this.setState({tracks: []})
    let artists = this.state.artists.split(',');
    artists.map(artistName => (
      spotifyWebApi.searchArtists(artistName) 
      .then((res) => {
        let artistId = res.artists.items[0].id
        spotifyWebApi.getArtistTopTracks(artistId, "CA", { limit: 15 })
        .then((res) => {
          for (let i = 0; i < res.tracks.length; i++) {
            this.setState(state => {
              const tracks = state.tracks.concat(res.tracks[i])
              const trackuri = state.trackuri.concat(res.tracks[i].uri)
              return {tracks, trackuri}
            })
          }
        })
      }) 
    ))
  }

  skipNext() {
    spotifyWebApi.skipToNext({device_id: this.state.device_id, })
    .then(() => {
      spotifyWebApi.getMyCurrentPlaybackState()
      .then((res) => {
        this.setState({
          nowPlaying: {
            name: res.item.name,
            image: res.item.album.images[0].url
          }
        })
      })
    })
  }

  skipPrev() {
    spotifyWebApi.skipToPrevious({device_id: this.state.device_id, })
    .then(() => {
      spotifyWebApi.getMyCurrentPlaybackState()
      .then((res) => {
        this.setState({
          nowPlaying: {
            name: res.item.name,
            image: res.item.album.images[0].url
          }
        })
      })
    })
  }

  handlePlaylistName(event) {
    this.setState({
      playlistName: event.target.value
    })
  }

  handleCreatePlaylist() {
    spotifyWebApi.createPlaylist(this.state.user_id, {name: this.state.playlistName})
    .then((res) => {
      spotifyWebApi.addTracksToPlaylist(this.state.user_id, res.id, this.state.trackuri)
    })
  }

  handleLogout() {
    spotifyWebApi.setAccessToken(null)
    this.setState({
      loggedIn: false
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Spotify Playlist Generator</h1>
        {this.state.loggedIn ? "" : 
        <a href="http://localhost:8888">
          <button>
            Log into Spotify
          </button>
        </a>
        }
        {this.state.loggedIn ? 

        <div>
          <div>
            Now playing: {this.state.nowPlaying.name}
          </div>
          <div>
            <img src={this.state.nowPlaying.image} style={{width: 100}} />
          </div>
          <br/>
          <button onClick={() => this.skipPrev()}>
            Prev song
          </button>
          <button onClick={() => this.skipNext()}>
            Next song
          </button>
          <form>
            <input 
              type="text" 
              placeholder="Enter artists name" 
              value={this.state.artists} 
              onChange={this.handleSearchChange}
            />
            <button type="button" onClick={() => this.handleSearchArtistId()}>
              Generate Playlist
            </button>
          </form>

          <div>
            {this.state.tracks.map(track => (
              <li key={track.id}>{track.name}</li>
            ))}
            { this.state.tracks.length > 0 ? 
            <div>
              <input
                type="text"
                placeholder="Playlist Name"
                value={this.state.playlistName}
                onChange={this.handlePlaylistName}
              /> 
              <button onClick={() => this.handleCreatePlaylist()}>
              Create playlist
              </button>
            </div>
          : ""}
          </div>
          <button onClick={() => this.handleLogout()}>
            Logout
          </button>
        </div> : ""}
      </div> 
    );
  }
}

export default App;
